import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-calc-data',
  templateUrl: './calc-data.component.html'
})
export class CalcDataComponent {
  public forecasts: WeatherForecast[] = [];
  txtcalulo = '';
  constructor() {
    //var baseUrlExterna = "https://localhost:44365/";
    //http.get<WeatherForecast[]>(baseUrlExterna + 'api/WeatherForecast').subscribe(result => {
    //  this.forecasts = result;
    //}, error => console.error(error));http: HttpClient, @Inject('BASE_URL') baseUrl: string
  }

  onKey(event: any) { // without type info
    this.txtcalulo += event.target.value;
   
  }
}

interface WeatherForecast {
  date: string;
  temperatureC: number;
  temperatureF: number;
  summary: string;
}
<h1 id="tableLabel">Teste de Cáculo</h1>


