import { Component, Inject } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, map } from 'rxjs';

@Component({
  selector: 'app-calc-data',
  templateUrl: './calc-data.component.html'
})
export class CalcDataComponent {
  value = {};
  public forecasts: Calculo[] = [];
  constructor(private http: HttpClient) {
    this.getValues();
  }


  ngOnInit() {
  }

  getValues() {
    var baseUrlExterna = "https://localhost:44365/";
    this.http.get<Calculo[]>(baseUrlExterna + 'api/Calculo').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));
  
  }
  //public forecasts: Calculo[] = [];

  //constructor(private http: HttpClient) {
  //  const url = 'https://localhost:44365/api/Calculo';
  //  this.http.get<any[]>(url).subscribe(result => {
  //  console.warn(result);
  //  });
  //}

  //onKey(event: any) { // without type info
  //  this.txtcalulo += event.target.value;
  //this.forecasts = 
  //}
  onSubmit(formValue: any): void {
    console.warn(formValue);
    //const body = { name: 'varejo_user', adress: 'w6h5xgtl' }
    //const headers = new Headers();
    //headers.append('Content-Type', 'application/x-www-form-urlencoded');


    let headers = new HttpHeaders();
    headers.append('Access-Control-Allow-Methods', 'GET, POST, OPTIONS, PUT, PATCH, DELETE');
    headers.append('Content-Type', 'application/x-www-form-urlencoded');

  
    let body = new URLSearchParams();
    body.set('name', 'varejo_user');
    body.set('adress', 'varejo_user');

    this.http.post<any>('https://localhost:44365/api/Calculo',
      body,
      { headers: headers, observe: 'response' })
      .pipe(
        map((response: { body: any; status: any; }) => ({ data: response.body, status: response.status }))
      )
  
    

    //const headers = { 'Authorization': 'Bearer my-token', 'My-Custom-Header': 'foobar' };
    //const body = { title: 'Angular POST Request Example' };
    //this.http.post<any>('https://localhost:44365/api/Calculo', formValue, { headers }).subscribe(data => {
    //});

    //const url = 'https://localhost:44365/api/Calculo';
    //this.http.post('https://localhost:44365/api/Calculo', formValue)
    //  .subscribe((result) => { console.warn("result", result)})

  }

   


}
 


interface Calculo {
  ValorBruto: string;
  ValorLiquido: string;

}

<h1 id="tableLabel">Teste de Cáculo</h1>


<form #userPost="ngForm" (ngSubmit)="onSubmit(userPost.value)">
  <input type="text" name="name" ngModel placeholder="name" /><br /><br />
  <input type="text" name="adress" ngModel placeholder="adress" /><br /><br />
  <input type="text" name="email" ngModel placeholder="email" /><br /><br />
  <input type="text" name="rating" ngModel placeholder="rating" /><br /><br />
  <button type="submit">Submit Data</button>
</form>

<p *ngIf="!forecasts"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="forecasts">
  <thead>
    <tr>
      <th>Valor Bruto</th>
      <th>Valor Liquido</th>
    
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let forecast of forecasts">
      <td>{{ forecast.ValorBruto }}</td>
      <td>{{ forecast.ValorBruto }}</td>
    </tr>
  </tbody>
</table>
